import React from "react";
import AliceCarousel from "react-alice-carousel";
import "react-alice-carousel/lib/alice-carousel.css";

const HomePage = () => {
  const handleDragStart = (e) => e.preventDefault();

  const responsive = {
    568: { items: 1 },
    720: { items: 2 },
    1024: { items: 3 },
  };

  const height = "300px";
  const width = "500px";

  const items = [
    <img
      src="/Big%20Daddy.jpg"
      alt="Big Daddy"
      onDragStart={handleDragStart}
      className="image-carousel"
      style={{ height: height, width: width }}
    />,
    <img
      src="/Big%20Sister.jpg"
      alt="Big Sister"
      onDragStart={handleDragStart}
      className="image-carousel"
      style={{ height: height, width: width }}
    />,
    <img
      src="/Columbia.jpg"
      alt="Columbia"
      onDragStart={handleDragStart}
      className="image-carousel"
      style={{ height: height, width: width }}
    />,
  ];

  return (
    <React.Fragment>
      <div>
        <AliceCarousel
          mouseTracking
          // autoWidth
          // autoHeight
          autoPlay
          autoPlayDirection={"ltr"}
          autoPlayInterval={3500}
          infinite
          items={items}
          responsive={responsive}
        />
      </div>

      <style jsx>{`
        div {
          width: 50%;
          height: 400px;
          margin: 0 auto;
        }
      `}</style>
    </React.Fragment>
  );
};

export default HomePage;
